import React, { useState } from 'react'

export default function TextForm(props) {
    const [itext,setText]=useState("Enter text here");

    const handleUpClick=()=>{

        setText(itext.toUpperCase());
    }
    const textChange=(event)=>{
        // console.log("Onchange")
        setText(event.target.value)
    }

    return (
        <>
            <div className="mb-3">
                <label htmlFor="exampleFormControlTextarea1" className="form-label">{props.text}</label>
                <textarea className="form-control" id="mybox" rows="8" value={itext} onChange={textChange}></textarea>
            </div>
            <button className="btn btn-primary"  onClick={handleUpClick}>Convert to uppercase</button>

        </>
    )
}
