// import logo from './logo.svg';
import './App.css';
import Alert from './components/Alert';
import About from './components/About';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import React,{useState} from 'react'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom"


function App() {
  const[mode,setMode]=useState("light")
  const[alert,setalert]=useState(null)

  const showalert=(type,message)=>{
    setalert({
      type:type,
      message: message
    })
    setTimeout(()=>{
      setalert(null)
    },1000)
  }

  const togglemode=()=>{
    if(mode==='light'){
      setMode('dark')
      // console.log(mode)
      document.body.style.backgroundColor='grey';
      showalert("Success","Dark mode activated!!")
    }
    else{
      setMode('light')
      document.body.style.backgroundColor='white';
      showalert("Success","Light mode activated!!")
    }
  }
  return (
    <Router>
    <>
    <Navbar title="textutil" textabout="about" mode={mode} togglemode={togglemode}/>
    <Alert alert={alert}/>
    <Switch>
      <Route exact path="/">
          <div className="container"><TextForm text="Text converter" mode={mode} alert={alert} showalert={showalert}/></div>
      </Route>
      <Route exact path="/about">

          <About/>
      </Route>

    </Switch>

    </>
    </Router>
    // -->jsx
  );
}

export default App;
