import React, { useState } from 'react'

export default function TextForm(props) {
    const [itext,setText]=useState("Enter text here");
    return (
        <>
            <div className="mb-3">
                <label for="exampleFormControlTextarea1" className="form-label">{props.text}</label>
                <textarea className="form-control" id="mybox" rows="8" value={itext}></textarea>
            </div>
            <button class="btn btn-primary" >Convert to uppercase</button>

        </>
    )
}
